import React from "react";
import { useParams } from "react-router-dom";
import "../../../styles/OperacaoDetalhes.css";

const operacoesDetalhes = {
  "cri-ema": {
    cidade: "Barra dos Coqueiros",
    ifm: 0.769647,
    rgsd: 0.564435,
    garantias: "37.828.734,68",
    duration: 2.48,
  },
  "cri-vanvera": {
    cidade: "Ariquemes",
    ifm: 2.541843,
    rgsd: 2.029735,
    garantias: "49.716.708,08",
    duration: 3.44,
  },
};

const OperacaoDetalhes = () => {
  const { nome } = useParams();
  const dados = operacoesDetalhes[nome];

  return (
    <div className="operacao-container">
      <h2 className="operacao-title">{nome.toUpperCase()}</h2>
      {dados ? (
        <div className="indicadores-container">
          <div className="indicador-card">
            <div className="indicador-header">Cidade</div>
            <div className="indicador-valor">{dados.cidade}</div>
          </div>
          <div className="indicador-card">
            <div className="indicador-header">IFM (%)</div>
            <div className="indicador-valor">{dados.ifm}</div>
          </div>
          <div className="indicador-card">
            <div className="indicador-header">RGSD (%)</div>
            <div className="indicador-valor">{dados.rgsd}</div>
          </div>
          <div className="indicador-card">
            <div className="indicador-header">Garantias (R$)</div>
            <div className="indicador-valor">{dados.garantias}</div>
          </div>
          <div className="indicador-card">
            <div className="indicador-header">Duration</div>
            <div className="indicador-valor">{dados.duration}</div>
          </div>
        </div>
      ) : (
        <p className="operacao-loading">Dados n√£o encontrados.</p>
      )}
    </div>
  );
};

export default OperacaoDetalhes;
