import React, { useEffect, useState } from "react";
import "../../../styles/Life11.css";

const Macro = () => {
  const [dadosMacro, setDadosMacro] = useState({
    igpm: "Carregando...",
    ipca: "Carregando...",
    cdi: "Carregando...",
    selic: "Carregando...",
    ifix: "Carregando...",
    ibovespa: "Carregando..."
  });

  // Função para buscar os dados do Banco Central do Brasil
  const fetchBCBData = async (codigo) => {
    try {
      const response = await fetch(`https://api.bcb.gov.br/dados/serie/bc${codigo}/dados?formato=json`);
      if (!response.ok) throw new Error("Falha ao buscar dados do BCB");
      const data = await response.json();
      return data.length ? `${parseFloat(data[data.length - 1].valor).toFixed(2)}%` : "N/A"; // Último valor disponível
    } catch (error) {
      console.error(`Erro ao buscar código ${codigo}:`, error);
      return "Erro";
    }
  };

  // Função para buscar o IBOVESPA e IFIX do Yahoo Finance
  const fetchYahooData = async (symbol) => {
    try {
      const response = await fetch(`https://query1.finance.yahoo.com/v8/finance/chart/${symbol}?region=BR&lang=pt-BR`);
      if (!response.ok) throw new Error("Falha ao buscar dados do Yahoo Finance");
      const data = await response.json();
      return data.chart.result[0].meta.regularMarketPrice.toFixed(2);
    } catch (error) {
      console.error(`Erro ao buscar ${symbol}:`, error);
      return "Erro";
    }
  };

  useEffect(() => {
    const fetchAllData = async () => {
      const igpm = await fetchBCBData(189);
      const ipca = await fetchBCBData(433);
      const cdi = await fetchBCBData(12);
      const selic = await fetchBCBData(11);
      const ifix = await fetchYahooData("^IFIX");
      const ibovespa = await fetchYahooData("^BVSP");

      setDadosMacro({ igpm, ipca, cdi, selic, ifix, ibovespa });
    };

    fetchAllData();
  }, []);

  return (
    <div className="container">
      <h1>Macro</h1>
      <p>Acompanhe nossas análises macroeconômicas e tendências do mercado.</p>

      <table className="macro-table">
        <thead>
          <tr>
            <th>Indicador</th>
            <th>Valor Atual</th>
          </tr>
        </thead>
        <tbody>
          <tr><td>IGP-M</td><td>{dadosMacro.igpm}</td></tr>
          <tr><td>IPCA</td><td>{dadosMacro.ipca}</td></tr>
          <tr><td>CDI</td><td>{dadosMacro.cdi}</td></tr>
          <tr><td>SELIC</td><td>{dadosMacro.selic}</td></tr>
          <tr><td>IFIX</td><td>{dadosMacro.ifix}</td></tr>
          <tr><td>IBOVESPA</td><td>{dadosMacro.ibovespa}</td></tr>
        </tbody>
      </table>
    </div>
  );
};

export default Macro;
