import React, { useEffect, useState } from "react";

const Macro = () => {
  const [dadosMacro, setDadosMacro] = useState({
    igpm: "Carregando...",
    ipca: "Carregando...",
    cdi: "Carregando...",
    selic: "Carregando...",
    ifix: "Carregando...",
    ibovespa: "Carregando..."
  });

  // Função para buscar dados macroeconômicos na BrasilAPI
  const fetchBrasilAPI = async (indicador) => {
    try {
      const response = await fetch(`https://brasilapi.com.br/api/taxas/v1/${indicador}`);
      if (!response.ok) throw new Error(`Erro ao buscar ${indicador}`);
      const data = await response.json();
      console.log(`✅ ${indicador}:`, data); // Debugando no console
      return data.valor ? `${parseFloat(data.valor).toFixed(2)}%` : "N/A";
    } catch (error) {
      console.error(`❌ Erro ao buscar ${indicador}:`, error);
      return "Erro ao carregar";
    }
  };

  // Função para buscar IBOVESPA e IFIX do Yahoo Finance
  const fetchYahooData = async (symbol) => {
    try {
      const response = await fetch(`https://query1.finance.yahoo.com/v8/finance/chart/${symbol}?region=BR&lang=pt-BR`);
      if (!response.ok) throw new Error(`Erro ao buscar ${symbol}`);
      const data = await response.json();
      console.log(`✅ ${symbol}:`, data); // Debugando no console
      return data.chart?.result?.[0]?.meta?.regularMarketPrice
        ? data.chart.result[0].meta.regularMarketPrice.toFixed(2)
        : "N/A";
    } catch (error) {
      console.error(`❌ Erro ao buscar ${symbol}:`, error);
      return "Erro ao carregar";
    }
  };

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        const igpm = await fetchBrasilAPI("igpm");
        const ipca = await fetchBrasilAPI("ipca");
        const cdi = await fetchBrasilAPI("cdi");
        const selic = await fetchBrasilAPI("selic");
        const ifix = await fetchYahooData("IFIX.SA");  // Código correto do IFIX no Yahoo
        const ibovespa = await fetchYahooData("^BVSP"); // Código correto do IBOVESPA no Yahoo

        setDadosMacro({ igpm, ipca, cdi, selic, ifix, ibovespa });
      } catch (error) {
        console.error("Erro ao carregar dados:", error);
      }
    };

    fetchAllData();
  }, []);

  return (
    <div className="container">
      <h1>Macro</h1>
      <p>Acompanhe nossas análises macroeconômicas e tendências do mercado.</p>

      <table className="macro-table">
        <thead>
          <tr>
            <th>Indicador</th>
            <th>Valor Atual</th>
          </tr>
        </thead>
        <tbody>
          <tr><td>📌 IGP-M</td><td>{dadosMacro.igpm}</td></tr>
          <tr><td>📌 IPCA</td><td>{dadosMacro.ipca}</td></tr>
          <tr><td>📌 CDI</td><td>{dadosMacro.cdi}</td></tr>
          <tr><td>📌 SELIC</td><td>{dadosMacro.selic}</td></tr>
          <tr><td>📌 IFIX</td><td>{dadosMacro.ifix}</td></tr>
          <tr><td>📌 IBOVESPA</td><td>{dadosMacro.ibovespa}</td></tr>
        </tbody>
      </table>
    </div>
  );
};

export default Macro;
