import React, { useState } from "react";
import "../styles/Time.css";

const equipe = [
  { id: 1, nome: "Pessoa 1", cargo: "CEO", cor: "purple", contato: { email: "ceo@empresa.com", telefone: "(11) 99999-9999" }, descricao: "Responsável pela visão estratégica e crescimento da empresa.", subordinados: [2, 3, 4] },
  { id: 2, nome: "Pessoa 2", cargo: "Diretor Financeiro", cor: "blue", contato: { email: "financeiro@empresa.com", telefone: "(11) 98888-8888" }, descricao: "Gerencia a área financeira e estratégias de investimento.", subordinados: [5, 6] },
  { id: 3, nome: "Pessoa 3", cargo: "Diretor de Operações", cor: "blue", contato: { email: "operacoes@empresa.com", telefone: "(11) 97777-7777" }, descricao: "Lidera as operações e otimização de processos.", subordinados: [7, 8] },
  { id: 4, nome: "Pessoa 4", cargo: "Diretor de Tecnologia", cor: "blue", contato: { email: "tecnologia@empresa.com", telefone: "(11) 96666-6666" }, descricao: "Gerencia a área de tecnologia e desenvolvimento de produtos.", subordinados: [9, 10] },
  { id: 5, nome: "Pessoa 5", cargo: "Analista Financeiro", cor: "green", contato: { email: "analista@empresa.com", telefone: "(11) 95555-5555" }, descricao: "Analisa dados financeiros e prepara relatórios gerenciais.", subordinados: [] },
  { id: 6, nome: "Pessoa 6", cargo: "Analista Contábil", cor: "green", contato: { email: "contabil@empresa.com", telefone: "(11) 94444-4444" }, descricao: "Responsável por balanços e relatórios contábeis.", subordinados: [] },
  { id: 7, nome: "Pessoa 7", cargo: "Gerente de Logística", cor: "green", contato: { email: "logistica@empresa.com", telefone: "(11) 93333-3333" }, descricao: "Gerencia a distribuição e cadeia de suprimentos.", subordinados: [] },
  { id: 8, nome: "Pessoa 8", cargo: "Gerente de Produção", cor: "green", contato: { email: "producao@empresa.com", telefone: "(11) 92222-2222" }, descricao: "Lidera a produção e controle de qualidade.", subordinados: [] },
  { id: 9, nome: "Pessoa 9", cargo: "Desenvolvedor Frontend", cor: "orange", contato: { email: "frontend@empresa.com", telefone: "(11) 91111-1111" }, descricao: "Desenvolve interfaces web e aplicações interativas.", subordinados: [] },
  { id: 10, nome: "Pessoa 10", cargo: "Desenvolvedor Backend", cor: "orange", contato: { email: "backend@empresa.com", telefone: "(11) 90000-0000" }, descricao: "Desenvolve sistemas e bancos de dados.", subordinados: [] }
];

const Time = () => {
  const [detalhes, setDetalhes] = useState(null);

  const handleClick = (id) => {
    const membro = equipe.find((pessoa) => pessoa.id === id);
    setDetalhes(membro);
  };

  const renderHierarquia = (id) => {
    const membro = equipe.find((pessoa) => pessoa.id === id);
    if (!membro) return null;

    return (
      <div className="membro-container">
        {membro.subordinados.length > 0 && <div className="linha-superior"></div>}
        <div className={`membro ${membro.cor}`} onClick={() => handleClick(id)}>
          <div className="icon">👤</div>
          <span className="cargo">{membro.cargo}</span>
          <span className="nome">{membro.nome}</span>
        </div>
        <div className="subordinados">
          {membro.subordinados.map((subId) => (
            <div key={subId} className="subordinado-wrapper">
              <div className="linha-conector"></div>
              {renderHierarquia(subId)}
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="time-container">
      <h1>Nosso Time</h1>
      <p>Clique em um membro para ver mais detalhes.</p>
      <div className="hierarquia">{renderHierarquia(1)}</div>

      {detalhes && (
        <div className="detalhes">
          <h2>{detalhes.nome}</h2>
          <h3>{detalhes.cargo}</h3>
          <p>{detalhes.descricao}</p>
          <p><strong>Email:</strong> {detalhes.contato.email}</p>
          <p><strong>Telefone:</strong> {detalhes.contato.telefone}</p>
          <button onClick={() => setDetalhes(null)}>Fechar</button>
        </div>
      )}
    </div>
  );
};

export default Time;
