import React, { useEffect, useState } from "react";
import "../styles/Macro.css";


const Macro = () => {
  const [dadosMacro, setDadosMacro] = useState({
    igpm: "Carregando...",
    ipca: "Carregando...",
    cdi: "Carregando...",
    selic: "Carregando...",
    ifix: "Carregando...",
    ibovespa: "Carregando..."
  });

  // FunÃ§Ã£o para buscar dados macroeconÃ´micos na BrasilAPI
  const fetchBrasilAPI = async (indicador) => {
    try {
      const response = await fetch(`https://brasilapi.com.br/api/taxas/v1/${indicador}`);
      if (!response.ok) throw new Error(`Erro ao buscar ${indicador}`);
      const data = await response.json();
      console.log(`âœ… ${indicador}:`, data); // Debugando no console
      return data.valor ? `${parseFloat(data.valor).toFixed(2)}%` : "N/A";
    } catch (error) {
      console.error(`âŒ Erro ao buscar ${indicador}:`, error);
      return "Erro ao carregar";
    }
  };

  // FunÃ§Ã£o para buscar IBOVESPA e IFIX do Yahoo Finance
  const fetchYahooData = async (symbol) => {
    try {
      const response = await fetch(`https://query1.finance.yahoo.com/v8/finance/chart/${symbol}?region=BR&lang=pt-BR`);
      if (!response.ok) throw new Error(`Erro ao buscar ${symbol}`);
      const data = await response.json();

      // Corrigindo a extraÃ§Ã£o do preÃ§o
      const price = data.chart?.result?.[0]?.meta?.regularMarketPrice;
      if (!price) throw new Error(`PreÃ§o nÃ£o encontrado para ${symbol}`);

      console.log(`âœ… ${symbol}:`, price); // Debugando no console
      return price.toFixed(2);
    } catch (error) {
      console.error(`âŒ Erro ao buscar ${symbol}:`, error);
      return "Erro ao carregar";
    }
  };

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        const igpm = await fetchBrasilAPI("igpm");
        const ipca = await fetchBrasilAPI("ipca");
        const cdi = await fetchBrasilAPI("cdi");
        const selic = await fetchBrasilAPI("selic");
        const ifix = await fetchYahooData("IFIX.SA");  // CÃ³digo correto do IFIX no Yahoo
        const ibovespa = await fetchYahooData("^BVSP"); // CÃ³digo correto do IBOVESPA no Yahoo

        setDadosMacro({ igpm, ipca, cdi, selic, ifix, ibovespa });
      } catch (error) {
        console.error("Erro ao carregar dados:", error);
      }
    };

    fetchAllData();
  }, []);

  return (
    <div className="container">
      <h1>Macro</h1>
      <p>Acompanhe nossas anÃ¡lises macroeconÃ´micas e tendÃªncias do mercado.</p>

      <table className="macro-table">
        <thead>
          <tr>
            <th>Indicador</th>
            <th>Valor Atual</th>
          </tr>
        </thead>
        <tbody>
          <tr><td>ðŸ“Œ IGP-M</td><td>{dadosMacro.igpm}</td></tr>
          <tr><td>ðŸ“Œ IPCA</td><td>{dadosMacro.ipca}</td></tr>
          <tr><td>ðŸ“Œ CDI</td><td>{dadosMacro.cdi}</td></tr>
          <tr><td>ðŸ“Œ SELIC</td><td>{dadosMacro.selic}</td></tr>
          <tr><td>ðŸ“Œ IFIX</td><td>{dadosMacro.ifix}</td></tr>
          <tr><td>ðŸ“Œ IBOVESPA</td><td>{dadosMacro.ibovespa}</td></tr>
        </tbody>
      </table>
    </div>
  );
};

export default Macro;
