import React, { useState } from "react";
import "../styles/Time.css";

const equipe = [
  {
    id: 1,
    nome: "Pessoa 1",
    cargo: "CEO",
    contato: { email: "ceo@empresa.com", telefone: "(11) 99999-9999" },
    descricao: "Responsável pela visão estratégica e crescimento da empresa.",
    subordinados: [2, 3], // IDs dos subordinados diretos
  },
  {
    id: 2,
    nome: "Pessoa 2",
    cargo: "Diretor Financeiro",
    contato: { email: "financeiro@empresa.com", telefone: "(11) 98888-8888" },
    descricao: "Gerencia a área financeira e estratégias de investimento.",
    subordinados: [4, 5],
  },
  {
    id: 3,
    nome: "Pessoa 3",
    cargo: "Diretor de Operações",
    contato: { email: "operacoes@empresa.com", telefone: "(11) 97777-7777" },
    descricao: "Lidera as operações e otimização de processos.",
    subordinados: [6],
  },
  {
    id: 4,
    nome: "Pessoa 4",
    cargo: "Analista Financeiro",
    contato: { email: "analista@empresa.com", telefone: "(11) 96666-6666" },
    descricao: "Analisa dados financeiros e prepara relatórios gerenciais.",
    subordinados: [],
  },
  {
    id: 5,
    nome: "Pessoa 5",
    cargo: "Analista Contábil",
    contato: { email: "contabil@empresa.com", telefone: "(11) 95555-5555" },
    descricao: "Responsável por balanços e relatórios contábeis.",
    subordinados: [],
  },
  {
    id: 6,
    nome: "Pessoa 6",
    cargo: "Gerente de Logística",
    contato: { email: "logistica@empresa.com", telefone: "(11) 94444-4444" },
    descricao: "Gerencia a distribuição e cadeia de suprimentos.",
    subordinados: [],
  },
];

const Time = () => {
  const [detalhes, setDetalhes] = useState(null);

  // Função para abrir os detalhes de um membro ao clicar
  const handleClick = (id) => {
    const membro = equipe.find((pessoa) => pessoa.id === id);
    setDetalhes(membro);
  };

  // Função para renderizar a árvore de hierarquia
  const renderHierarquia = (id) => {
    const membro = equipe.find((pessoa) => pessoa.id === id);
    if (!membro) return null;

    return (
      <div className="membro">
        <div className="membro-info" onClick={() => handleClick(id)}>
          <span className="cargo">{membro.cargo}</span>
          <span className="nome">{membro.nome}</span>
        </div>
        <div className="subordinados">
          {membro.subordinados.map((subId) => renderHierarquia(subId))}
        </div>
      </div>
    );
  };

  return (
    <div className="time-container">
      <h1>Nosso Time</h1>
      <p>Clique em um membro para ver mais detalhes.</p>

      <div className="hierarquia">{renderHierarquia(1)}</div>

      {detalhes && (
        <div className="detalhes">
          <h2>{detalhes.nome}</h2>
          <h3>{detalhes.cargo}</h3>
          <p>{detalhes.descricao}</p>
          <p><strong>Email:</strong> {detalhes.contato.email}</p>
          <p><strong>Telefone:</strong> {detalhes.contato.telefone}</p>
          <button onClick={() => setDetalhes(null)}>Fechar</button>
        </div>
      )}
    </div>
  );
};

export default Time;
