import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/Navbar.css";

const Navbar = () => {
  const [menuAberto, setMenuAberto] = useState(false);

  // Alterna o menu ao clicar no botão hambúrguer
  const toggleMenu = () => {
    setMenuAberto((prevState) => !prevState); // Corrige a atualização do estado
  };

  // Fecha o menu ao clicar em um link
  const fecharMenu = () => {
    setMenuAberto(false);
  };

  return (
    <nav className="navbar">
      <div className="nav-logo">LCP HUB</div>

      {/* Botão hambúrguer */}
      <button className="menu-toggle" onClick={toggleMenu} aria-label="Abrir Menu">
        ☰
      </button>

      {/* Menu de navegação */}
      <ul className={`nav-links ${menuAberto ? "active" : ""}`}>
        <li><Link to="/" onClick={fecharMenu}>Home</Link></li>
        <li><Link to="/fundos" onClick={fecharMenu}>Fundos</Link></li>
        <li><Link to="/relatorios" onClick={fecharMenu}>Relatórios</Link></li>
        <li><Link to="/noticias" onClick={fecharMenu}>Notícias</Link></li>
        <li><Link to="/time" onClick={fecharMenu}>Nosso Time</Link></li>
        <li><Link to="/contato" onClick={fecharMenu}>Contato</Link></li>
      </ul>
    </nav>
  );
};

export default Navbar;
