import React, { useEffect, useState } from "react";

const Macro = () => {
  const [dadosMacro, setDadosMacro] = useState({
    igpm: "Carregando...",
    ipca: "Carregando...",
    cdi: "Carregando...",
    selic: "Carregando...",
    ifix: "Carregando...",
    ibovespa: "Carregando..."
  });

  const token = 'SEU_TOKEN_AQUI'; // Substitua pelo seu token de autenticação

  const fetchDados = async (endpoint) => {
    try {
      const response = await fetch(endpoint, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (!response.ok) throw new Error("Erro na resposta da API");
      const data = await response.json();
      return data.length ? `${parseFloat(data[data.length - 1].value).toFixed(2)}%` : "N/A";
    } catch (error) {
      console.error(`Erro ao buscar dados de ${endpoint}:`, error);
      return "Erro";
    }
  };

  useEffect(() => {
    const fetchAllData = async () => {
      const igpm = await fetchDados('https://api.dadosdemercado.com.br/v1/macro/igp-m');
      const ipca = await fetchDados('https://api.dadosdemercado.com.br/v1/macro/ipca');
      const cdi = await fetchDados('https://api.dadosdemercado.com.br/v1/macro/cdi');
      const selic = await fetchDados('https://api.dadosdemercado.com.br/v1/macro/selic');
      const ifix = await fetchDados('https://api.dadosdemercado.com.br/v1/indices/ifix');
      const ibovespa = await fetchDados('https://api.dadosdemercado.com.br/v1/indices/ibovespa');

      setDadosMacro({ igpm, ipca, cdi, selic, ifix, ibovespa });
    };

    fetchAllData();
  }, []);

  return (
    <div className="container">
      <h1>Macro</h1>
      <p>Acompanhe nossas análises macroeconômicas e tendências do mercado.</p>

      <table className="macro-table">
        <thead>
          <tr>
            <th>Indicador</th>
            <th>Valor Atual</th>
          </tr>
        </thead>
        <tbody>
          <tr><td>IGP-M</td><td>{dadosMacro.igpm}</td></tr>
          <tr><td>IPCA</td><td>{dadosMacro.ipca}</td></tr>
          <tr><td>CDI</td><td>{dadosMacro.cdi}</td></tr>
          <tr><td>SELIC</td><td>{dadosMacro.selic}</td></tr>
          <tr><td>IFIX</td><td>{dadosMacro.ifix}</td></tr>
          <tr><td>IBOVESPA</td><td>{dadosMacro.ibovespa}</td></tr>
        </tbody>
      </table>
    </div>
  );
};

export default Macro;
